// src/components/MusicPlayerCard.js
import React, { useState, useRef } from 'react';
import { FaPlay, FaPause } from 'react-icons/fa';
import './MusicPlayerCard.css';

const MusicPlayerCard = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);

  const togglePlayPause = () => {
    const audio = audioRef.current;
    if (isPlaying) {
      audio.pause();
    } else {
      audio.play();
    }
    setIsPlaying(!isPlaying);
  };

  return (
    <div className="music-player-card">
      <div className="album-art">
        <img
          src={require('../clip_art.jpeg')}  // Replace with your image path
          alt="Clip Art"
        />
        <div className="controls">
          {/* Here's where the play/pause button goes */}
          <button className="play-pause-button" onClick={togglePlayPause}>
            {isPlaying ? <FaPause /> : <FaPlay />}
          </button>
        </div>
      </div>
      <div className="song-info">
        <h3>Paranoya</h3>
        <p>Koorosh</p>
      </div>
      <audio
        ref={audioRef}
        src="https://www.soundhelix.com/examples/mp3/SoundHelix-Song-1.mp3"  // Your audio source
      ></audio>
    </div>
  );
};

export default MusicPlayerCard;
