// src/components/MusicPlayerCard.js
import React, { useState, useRef, useEffect } from 'react';
import { FaPlay, FaPause } from 'react-icons/fa';
import { FastAverageColor } from 'fast-average-color';
import './MusicPlayerCard.css';

const MusicPlayerCard = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [progress, setProgress] = useState(0);
  const [dominantColor, setDominantColor] = useState('#f0f0f5');
  const audioRef = useRef(null);
  const imgRef = useRef(null);

  useEffect(() => {
    const fac = new FastAverageColor();

    if (imgRef.current) {
      fac.getColorAsync(imgRef.current)
        .then(color => {
          setDominantColor(color.hex);
          document.documentElement.style.setProperty('--dominant-color', color.hex); // Set global CSS variable
        })
        .catch(e => console.error(e));
    }
  }, []);

  const togglePlayPause = () => {
    const audio = audioRef.current;
    if (isPlaying) {
      audio.pause();
    } else {
      audio.play();
    }
    setIsPlaying(!isPlaying);
  };

  const handleTimeUpdate = () => {
    const audio = audioRef.current;
    const progress = (audio.currentTime / audio.duration) * 100;
    setProgress(progress);
  };

  return (
    <div className="music-card" style={{ backgroundColor: dominantColor }}>
      <img
        ref={imgRef}
        src={require('../clip_art.webp')}
        className="album-art"
        alt="Clip Art"
        crossOrigin="anonymous"
      />
      <div className="progress-container">
        <progress value={progress} max="100"></progress>
        <span className="time">2:15 / 3:20</span>
      </div>
      <div className="play-pause-button" onClick={togglePlayPause}>
        {isPlaying ? <FaPause /> : <FaPlay />}
      </div>
      <audio
        ref={audioRef}
        src="https://www.soundhelix.com/examples/mp3/SoundHelix-Song-1.mp3"
        onTimeUpdate={handleTimeUpdate}
      ></audio>
    </div>
  );
};

export default MusicPlayerCard;
